import React from 'react'
import { HeadPost } from '../HeadPost'

interface MetaInfo {
  title: string
  category: string
  author: string
  description: string
  date: string
  readTime: number
}

interface BlogImageProps {
  image: string
  meta: MetaInfo
}

export default function BlogImage({image, meta} : BlogImageProps) {
  return (
    <div className="h-84 lg:h-screen w-full flex flex-col items-center justify-end" 
      style={{
        backgroundImage: `linear-gradient(to bottom, rgba(0,0,0,0) 65%,rgba(0,0,0,0.75)),url('${image}')`, 
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover'
        }}>
      <div className="hidden lg:block text-center">
        <HeadPost meta={meta} isBlogPost />
      </div>
    </div>
  )
}
