interface MetaInfo {
  title: string
  category: string
  author: string
  description: string
  date: string
  readTime: number
}

interface HeadPostProps {
  meta: MetaInfo
  isBlogPost: boolean
}
export const HeadPost = ({ meta, isBlogPost } : HeadPostProps) => (
  <>
    <div className="lg:hidden text-center">
      <p className="text-coral font-rubik text-sm mb-2">Planning</p>
<h3 className="font-nunito font-semibold text-h4">{meta.title}</h3>
      <div className="flex items-center justify-center mb-4">  
        <p className="font-rubik text-sm">{meta.author}</p>
        <p className="text-xs pb-1 mx-4">|</p>
        <p className="font-rubik text-gray-500 text-sm">{meta.date}</p>
      </div>
    </div>
    <style jsx>
      {`
        h1 {
          font-size: 1.5rem;
          font-weight: 700;
          color: #f39c12;
        }
        .great-title {
            font-size: 2rem;
        }
        .details span {
          color: #bdbdbd;
          margin-right: 1rem;
        }
        .details {
          margin-bottom: 1rem;
        }
      `}
    </style>
  </>
)
