interface MetaInfo {
  title: string
  category: string
  author: string
  description: string
  date: string
  readTime: number
}

interface HeadPostProps {
  meta: MetaInfo
  isBlogPost: boolean
}
export const HeadPost = ({ meta, isBlogPost } : HeadPostProps) => (
  <>
    <div className="hidden lg:block text-center">
        <p className="text-white font-rubik text-sm mb-2">Planning</p>
        <h3 className="font-nunito text-white font-semibold text-h4">Head to Toe Blueprints for Styling a Classic Themed Wedding</h3>
        <div className="flex items-center justify-center mb-4">  
          <p className="text-white font-rubik text-sm">by Penci Design</p>
          <p className="text-white text-xs pb-1 mx-4">|</p>
          <p className="text-white font-rubik text-sm">June 23, 2019</p>
        </div>
      </div>
    <style jsx>
      {`
        h1 {
          font-size: 1.5rem;
          font-weight: 700;
          color: #f39c12;
        }
        .great-title {
            font-size: 2rem;
        }
        .details span {
          color: #bdbdbd;
          margin-right: 1rem;
        }
        .details {
          margin-bottom: 1rem;
        }
      `}
    </style>
  </>
)
