    {
        isBlogPost? null: <p>{meta.description}</p>
    }  
      <span>{meta.date}</span>
      <span role='img' aria-label='one coffee'>
        â˜• {meta.readTime + ' min read'}
      </span>
    </div>
    <style jsx>
      {`
        h1 {
          font-size: 1.5rem;
          font-weight: 700;
          color: #f39c12;
        }
        .great-title {
            font-size: 2rem;
        }
        .details span {
          color: #bdbdbd;
          margin-right: 1rem;
